package dataTypesandVariables;

public class ArithmeticOperators {

	public static void main(String[] args) 
	{
		
		int a=10;
		int b=20;
		
		//+operator
		System.out.println(a+b);
		
		//-operator
		System.out.println(a-b);
		
		//multiply
		System.out.println(a*b);
		
		//divide
		System.out.println(b/a);
		
		//% module
		System.out.println(17%18);
		
		// TODO Auto-generated method stub

	}

}
