package dataTypesandVariables;

public class Class2 {

	public static void main(String[] args) 
	{
		int a=10;
		int b=10;
		
		//<, ==, <=, >=, != relational operators
		if(a<b)
		{
			System.out.println("a is greater than b");
		}
			else
			{
			System.out.println("b is greater than a");
			}
		// TODO Auto-generated method stub
		if(a>b)
		{
			System.out.println("a is greater than b");
		}
			else
			{
			System.out.println("b is greater than a");
			}
	}

}
